		exit;
	}

	// hook: soe_posts_before_insert
	if(function_exists('soe_posts_before_insert')) {
		$args = [];
		if(!soe_posts_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('soe_posts', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('soe_posts', $recID, calculated_fields()['soe_posts']);

	// hook: soe_posts_after_insert
	if(function_exists('soe_posts_after_insert')) {
		$res = sql("SELECT * FROM `soe_posts` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
